import { Component, OnInit, } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Player } from '../models/interfaces.model';

@Component({
  selector: 'app-logging',
  templateUrl: './logging.component.html',
  styleUrls: ['./logging.component.css']
})
export class LoggingComponent implements OnInit{
  private baseUrl: string = 'http://localhost:3000/player/';
  inputNombre: string = '';

  constructor (private http: HttpClient ){}

  ngOnInit(): void{
    console.log('proba');

  }

  registroUser(): void{
    //Al pulsar el botón de jugar se registra el jugador con el nombre.
    let player = this.insertPlayer(this.inputNombre)
console.log(player);

    //sessionStorage.setItem(player,)
  }

  insertPlayer(nombre: string):{
    const nombrePlayer = { nombre: nombre};
    this.http.post<Player>(this.baseUrl, nombrePlayer).subscribe(
      data => {
        console.log(`Datos del juego con ID :`, data);
      },
      error => {
        console.error(`Error al obtener el juego con ID :`, error);
      });
  }
}
