    <span class="loader"></span>
</div> -->

<!-- ALERTA DE VICTORIA -->
<div class="alert"><a class="alert-win" href="#open-modal">👋 Basic CSS-Only Modal</a></div>


<!-- LOGICA DEL JUEGO -->
<div class="game-container">
  <!-- PLAYER X -->
  <div class="player-section bg-blue d-flex justify-content-center" [ngClass]="{'degradadoX': game?.turn === 'X'}">
    <div class="text-center mt-5" *ngIf="currentPlayer === 'X'">
      <div class="playerName">{{ player.nombre }}</div>
      <div class="turno" *ngIf="currentPlayer === game?.turn">TU TURNO</div>
    </div>
    <div class="text-center mt-5" *ngIf="currentPlayer === 'O'">
      <div class="playerName">{{ opponent?.nombre }}</div>
    </div>
  </div>


  <!-- PLAYER O -->
  <div class="player-section bg-purple d-flex justify-content-center" [ngClass]="{'degradadoY': game?.turn === 'O'}">
    <div  class="text-center mt-5" *ngIf="currentPlayer === 'O'">
        <div class="playerName">{{ player.nombre }}</div>
        <div class="turno" *ngIf="currentPlayer === game?.turn">TU TURNO</div>
    </div>
    <div  class="text-center mt-5" *ngIf="currentPlayer === 'X'">
        <div class="playerName">{{ opponent?.nombre }}</div>
    </div>
  </div>



  <!-- TABLERO -->
  <div class="board-container">


    <table class="tres-en-raya">
      <tr *ngFor="let row of [0, 1, 2]">
        <td *ngFor="let col of [0, 1, 2]" (click)="makeMove(row, col)">
          {{ board[row][col] }}
        </td>
      </tr>
