import { Component, OnInit } from '@angular/core';
import { SocketService } from '../socket.service'; // Asegúrate de importar tu servicio

@Component({
  selector: 'app-game',
  templateUrl: './game.component.html'
})
export class GameComponent implements OnInit {
  board: string[][] = [
    ['', '', ''],
    ['', '', ''],
    ['', '', '']
  ];
  currentPlayer: string = 'X';



  constructor(private socketService: SocketService) {
    // Escuchar movimientos de otros jugadores
    this.socketService.listenForMoves().subscribe((moveData: {row: number, col: number, player: string}) => {
      this.board[moveData.row][moveData.col] = moveData.player;
      this.currentPlayer = moveData.player === 'X' ? 'O' : 'X';
    });
  }

  makeMove(row: number, col: number): void {
    if (this.board[row][col] === '') {
      this.board[row][col] = this.currentPlayer;
      this.socketService.emitMove({ row, col, player: this.currentPlayer });
      this.currentPlayer = this.currentPlayer === 'X' ? 'O' : 'X';
    }
  }
}
<table>
  <tr *ngFor="let rowIndex of [0,1,2]">
    <td *ngFor="let colIndex of [0,1,2]" (click)="makeMove(rowIndex, colIndex)">
      {{ board[rowIndex][colIndex] }}
    </td>
  </tr>
</table>
