import { Component, OnInit, } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Player, Game } from '../models/interfaces.model';
import { Observable } from 'rxjs';
import { catchError } from 'rxjs/operators';
import { of } from 'rxjs';

@Component({
  selector: 'app-mesa',
  templateUrl: './mesa.component.html',
  styleUrls: ['./mesa.component.css']
})
export class MesaComponent implements OnInit {


  ngOnInit(): void {

  }

  @SubscribeMessage('movimiento-jugador')
handlePlayerMove(client: Socket, payload: any): void {
    // Lógica para manejar el movimiento del jugador
    // payload podría contener detalles como la posición del movimiento, el jugador, etc.

    // Por ahora, solo reenvía el movimiento al otro jugador
    client.broadcast.emit('movimiento-oponente', payload);
}

}
