    <div class="text-center mt-5">
      <div class="playerName">Martin</div>
      <div class="turno">TU TURNO</div>
    </div>
  </div>

  <div class="player-section bg-purple d-flex justify-content-center" [ngClass]="{'degradadoY': game?.turn === 'O'}">
    <div class="text-center mt-5">
      <div class="playerName">Carlos</div>
      <div></div>
    </div>
    <div *ngIf="currentPlayer === 'O'">
      <div>{{ player.nombre }}</div>
    </div>
  </div>

  <div class="board-container">
    <table class="tres-en-raya">
